<?php 	include 'admin/functions.php';	$config = 'admin/config.php';	if (file_exists($config)) {		include $config; 		$i = $GLOBALS['theme']; 		if (isset($_GET["post"])) {				$length = strlen($_GET["post"]);				if ($length == "1"){					$p = "0000".$_GET["post"];				}				else if ($length == "2"){					$p = "000".$_GET["post"];				} 				else if ($length = "3"){					$p = "00".$_GET["post"];				}				else if ($length == "4"){					$p = "0".$_GET["post"];				}				else if ($length == "5"){					$p = $_GET["post"];				}				include 'themes/'.$i.'/post.php';		}		else if (isset($_GET["q"])) {				include 'themes/'.$i.'/search.php';		} 		else {				include 'themes/'.$i.'/index.php';		} 		}	else {			header("Location: boot.php");	}?>